package oopIntro;

public class Category {
     int id;
     String name;
     
          
	public Category() {
		super();
		// TODO Auto-generated constructor stub
	}


	public Category(int id, String name) {
		super();
		this.id = id;
		this.name = name;
	}


	public int getId() {
		return id;
	}


	public void setId(int id) {
		this.id = id;
	}


	public String getName() {
		return name;
	}


	public void setName(String name) {
		this.name = name;
	}
     
     
}
